mapper.update:
update wms_ware_sku
set stock_locked=stock_locked + #{count}
where sku_id = #{skuId}
  and ware_id = #{wareId}
  and stock - wms_ware_sku.stock_locked >= #{count}

update wms_ware_sku
set stock_locked=stock_locked + #{count}
where sku_id = #{skuId}
  and ware_id = (
    select a.ware_id
    from (
             select ware_id
             from wms_ware_sku
             where sku_id = #{skuId}
               and stock - wms_ware_sku.stock_locked >= #{count} limit 0,1
         ) a
    limit 0,1
)
